import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import Axios from 'axios';
import Comment from './Comment.js';


import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';

import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import { red } from '@material-ui/core/colors';





const useStyles = makeStyles((({
  root: {
    flexGrow: 1,
    backgroundColor: red,
  },
})))





const [value, setValue] = this.sReact.useState(0);


class App extends Component {



  handleChange = (event, newValue) => {
    setValue(newValue);
  };


 a11yProps(index) {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
  }


  TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`simple-tabpanel-${index}`}
        aria-labelledby={`simple-tab-${index}`}
        {...other}
      >
        {value === index && (
          <Box p={3}>
            <Typography>{children}</Typography>
          </Box>
        )}
      </div>
    );
  }





  render(){


  return (
    <Router>
      <div>
        {/* <ul>
          <li>
            <Link to="/comment">comment</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
          <li>
            <Link to="/dashboard">Dashboard</Link>
          </li>
        </ul> */}


<AppBar position="static">
        <Tabs value={3} onChange={this.handleChange} aria-label="simple tabs example">
          <Tab label="Item One" {...this.a11yProps(0)} />
          <Tab label="Item Two" {...this.a11yProps(1)} />
          <Tab label="Item Three" {...this.a11yProps(2)} />
        </Tabs>
      </AppBar>
      <this.TabPanel value={3} index={0}>
        Item One
      </this.TabPanel>
      <this.TabPanel value={3} index={1}>
        Item Two
      </this.TabPanel>
      <this.TabPanel value={3} index={2}>
        Item Three
      </this.TabPanel>

        <hr />

        {

        }
        <Switch>
          <Route exact path="/comment">
            <Comment />
          </Route>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/dashboard">
            <Dashboard />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}
}







function About() {
  return (
    <div>
      <h2>About</h2>
    </div>
  );
}

function Dashboard() {
  return (
    <div>
      <h2>Dashboard</h2>
    </div>
  );
}

export default App;
